class OrderedObject {
public:
	virtual bool	operator<(const OrderedObject& otherObject) = 0;
	virtual bool	operator==(const OrderedObject& otherObject) = 0;

	bool	operator!=(const OrderedObject& otherObject)
				{ return !(*this == otherObject); }
};

