void SavePosition()
{
	// don't do it if we're the only NewsgroupWind
	for (int i=0; ; i++) {
		BWindow* wind = be_app->WindowAt(i);
		if (wind == NULL)
			break;
		if (wind != this && dynamic_cast<NewsrcWind*>(wind) != NULL)
			return;
		}

	Prefs()->SetRectPref("newsrcWind.frame", Frame());
}

