	ConnectionGetter()
		: connection(ConnectionPool::GetPool()->GetConnection()) {}
	~ConnectionGetter()
		{ ConnectionPool::GetPool()->ReleaseConnection(connection); }

	NNTPConnection*	Connection() { return connection; }

protected:
