}

void Action()
{
	// open the file
	BFile* file = new BFile(&fileRef, B_READ_WRITE);
	status_t err = file->InitCheck();
	if (err != B_NO_ERROR) {
		Error::ShowError("Couldn't open \"", fileRef.name, "\".", err);
		delete file;
		return;
		}

	// create the newsrc
	Newsrc* newsrc = new Newsrc(file);

